function optimizeImg(lowResUrl, htmlSectionId, imageIndex) {
    switch (htmlSectionId) {
        case 'mainSliderArea':
            return lowResUrl.replace('s72-c', 's1600');
        case 'recent1Area':
        case 'recent2Area':
            return lowResUrl.replace('s72-c', 's300');
        case 'recent5Area':
        case 'recent6Area':
            if (imageIndex == 1) {
                return lowResUrl.replace('s72-c', 's300');
            }
            break;
    }
    return lowResUrl;
}

